package com.xxl.util;


import com.xxl.common.enums.CommonConsts;
import sun.misc.BASE64Decoder;
import sun.misc.BASE64Encoder;

import javax.crypto.Cipher;
import javax.crypto.SecretKey;
import javax.crypto.SecretKeyFactory;
import javax.crypto.spec.DESedeKeySpec;


public final class ThreeDES {

    /**
     * 加密
     *
     * @param str
     * @param key
     * @return
     * @throws Exception
     * @see
     */
    public static String encryptThreeDESECB(String str, String key)
            throws Exception {
        if (StringUtil.isNil(str)) {
            str = "";
        }
        String temp = "";
        try {
            byte[] keys = new BASE64Decoder().decodeBuffer(key);

            DESedeKeySpec dks = new DESedeKeySpec(keys);
            SecretKeyFactory keyFactory = SecretKeyFactory.getInstance("DESede");
            SecretKey securekey = keyFactory.generateSecret(dks);

            Cipher cipher = Cipher.getInstance("DESede/ECB/PKCS5Padding");
            cipher.init(1, securekey);
            byte[] b = cipher.doFinal(str.getBytes(CommonConsts.UTF8));

            BASE64Encoder encoder = new BASE64Encoder();
            temp = encoder.encode(b).replaceAll("\r", "").replaceAll("\n", "");
            temp = HexUtil.toHexString(temp);
        } catch (Exception e) {
            e.printStackTrace();
            throw e;
        }
        return temp;
    }

    /**
     * 解密
     *
     * @param src 密文解密前hex转码前的密文
     * @param key key
     * @return
     * @throws Exception
     * @see
     */
    public static String decryptThreeDESECB(String src, String key)
            throws Exception {
        try {
            if (src.contains("\"")) {
                src = src.replace("\"", "");
            }


            src = HexUtil.fromHexString(src);


            BASE64Decoder decoder = new BASE64Decoder();

            byte[] bytesrc = decoder.decodeBuffer(src);
            byte[] keys = new BASE64Decoder().decodeBuffer(key);
            DESedeKeySpec dks = new DESedeKeySpec(keys);

            SecretKeyFactory keyFactory = SecretKeyFactory.getInstance("DESede");

            SecretKey securekey = keyFactory.generateSecret(dks);

            Cipher cipher = Cipher.getInstance("DESede/ECB/PKCS5Padding");

            cipher.init(2, securekey);

            byte[] retByte = cipher.doFinal(bytesrc);

            return new String(retByte, CommonConsts.UTF8);
        } catch (Exception e) {
            throw e;
        }
    }

    public static void main(String[] args)
            throws Exception {
        String str = "{\"userToken\":\"234\",\"version\":\"1\",\"mode\":\"1\",\"deptid\":\"020A\"}";
        String key = "7418b8be-d51b-4291-b8be-6fd8c3962211";
        String str1 = encryptThreeDESECB(str, key);
        System.out.println("密文：" + str1);

        String str3 = decryptThreeDESECB(
                "",
                key);
        System.out.println("明文：" + str3);
    }
}
